from flask import Flask, render_template, request, redirect, url_for, session, jsonifyimport secretsapp = Flask(__name__)secret_key = secrets.token_hex(32)app.secret_key = secret_key  # Secret key for session management# Dummy user data (replace with database integration)users = {    'Abhishek': 'Admin@123',    'Yash': 'Admin@123'}@app.route('/login', methods=['GET', 'POST'])def login():    if request.method == 'POST':        username = request.form['username']        password = request.form['password']        if username in users and users[username] == password:            session['username'] = username            return redirect(url_for('dashboard'))        else:            return jsonify({'error': 'Invalid username or password'}), 400  # Return JSON error response    return render_template('login.html')@app.route('/api/data')def get_data():    data = {'key': 'value'}  # Example data    return jsonify(data)@app.route('/dashboard')def dashboard():    if 'username' in session:        return f'<h1>Welcome, {session["username"]}!</h1>'    return redirect(url_for('login'))if __name__ == '__main__':    app.run(debug=True)